import React, { useState, useEffect } from "react";
import axios from "axios"; // For fetching reviews
import style from "./HomePage.css";

function HomePage() {
  const [reviews, setReviews] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  // Fetch reviews from the backend
  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await axios.get("http://localhost:5000/reviews");
        setReviews(response.data);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };

    fetchReviews();
  }, []);

  const toggleDropdown = () => {
    setDropdownOpen((prev) => !prev);
  };

  return (
    <div>
      {/* Navigation Bar */}
      <nav className="nav">
        <a href="#" className="logo">BookRev.</a>
        <ul className="nav-links">
          <li><a href="#">Home</a></li>
          <li><a href="/reviews">Review</a></li>
          <li className="dropdown">
            <a href="#" onClick={toggleDropdown}>Get Started</a>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <a href="/login">Login</a>
                <a href="/register">Register</a>
              </div>
            )}
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <div className="content">
        {/* Show default message if no reviews */}
        {reviews.length === 0 ? (
          <p className="default-message">
            Add your first book review to share your thoughts with the
            community!
          </p>
        ) : (
          <div className="reviews-container">
            <h3>Latest Reviews</h3>
            <ul className="reviews-list">
              {reviews.map((review) => (
                <li key={review._id} className="review-item">
                  <h4>{review.title}</h4>
                  <p>Author: {review.author}</p>
                  <p>Rating: {review.rating}</p>
                  <p>{review.reviewText}</p>
                  <div className="review-actions">
                    <a href={`/edit/${review._id}`}>Edit</a>
                    <button
                      onClick={async () => {
                        try {
                          await axios.delete(
                            `http://localhost:5000/reviews/${review._id}`
                          );
                          setReviews((prev) =>
                            prev.filter((r) => r._id !== review._id)
                          );
                        } catch (error) {
                          console.error("Error deleting review:", error);
                        }
                      }}
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;
