import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import HomePage from "./pages/Homepage";
import RegisterForm from "./components/RegisterForm";
import LoginForm from "./components/LoginForm";
import Review from "./components/Review";
import AddEditReview from "./components/AddEditReview";
import Navbar from "./components/Navbar";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Validate token on app load
  useEffect(() => {
    const validateToken = async () => {
      const token = localStorage.getItem("token");
      if (token) {
        try {
          const response = await fetch("http://localhost:5000/api/validate-token", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          if (response.ok) {
            setIsAuthenticated(true); // Token valid, update state
          } else {
            localStorage.removeItem("token"); // Remove invalid token
          }
        } catch (error) {
          console.error("Error validating token:", error);
          localStorage.removeItem("token");
        }
      }
    };
    validateToken();
  }, []);

  const logout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
  };

  // Protected Route Wrapper
  const ProtectedRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />;
  };

  return (
    <Router>
      <div>
        <Navbar isAuthenticated={isAuthenticated} logout={logout} />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route
            path="/login"
            element={<LoginForm setIsAuthenticated={setIsAuthenticated} />}
          />
          <Route
            path="/register"
            element={<RegisterForm setIsAuthenticated={setIsAuthenticated} />}
          />
          <Route
            path="/reviews"
            element={<ProtectedRoute element={<Review />} />}
          />
          <Route
            path="/edit"
            element={<ProtectedRoute element={<AddEditReview />} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
